export const COLORS = {
  primary: '#fbbf24',
  secondary: '#10b981',
  background: 'from-blue-900 to-purple-900',
  cardBackground: 'bg-white/10',
  textPrimary: 'text-white',
  textSecondary: 'text-blue-200'
};

export const CHART_CONFIG = {
  chartHeight: 300,
  lineChartHeight: 200,
  maxDataPoints: 10,
  colors: {
    volume: '#fbbf24',
    progress: '#10b981',
    grid: '#ffffff20',
    axis: '#ffffff80'
  }
};

export const VALIDATION = {
  maxSets: 20,
  maxReps: 50,
  maxWeight: 500,
  minSets: 1,
  minReps: 1,
  minWeight: 0
};

export const EXERCISE_CATEGORIES = {
  CHEST: 'Chest',
  BACK: 'Back',
  LEGS: 'Legs',
  SHOULDERS: 'Shoulders',
  ARMS: 'Arms'
};

export const DIFFICULTY_LEVELS = {
  BEGINNER: 'Beginner',
  INTERMEDIATE: 'Intermediate',
  ADVANCED: 'Advanced'
};
